export default {
  name: "FixedHeader",
  data () {
    return {
      description: "",
      isPosts: false
    };
  },
  props: {
    content: {
      type: Array,
      default: () => []
    }
  },
  computed: {
    title () {
      let t;
      switch (this.$route.path.slice(1, 6)) {
        case "posts":
          t = this.$page.title;
          this.isPosts = true;
          this.description = this.$page.lastUpdated
            ? "最后更新时间：" + this.$page.lastUpdated
            : "";
          break;
        case "all/":
          t = this.$themeConfig.menus.all || "时间归档";
          this.isPosts = false;
          if (this.content.length === 0) {
            return (this.description = "");
          }
          setTimeout(() => {
            this.description =
              this.content[this.content.length - 1].lastUpdated.slice(0, 7) +
              "~" +
              this.content[0].lastUpdated.slice(0, 7) +
              " ===>>> 共" +
              this.content.length +
              "篇";
          }, 20);
          break;
        case "tags/":
          t = "";
          this.isPosts = false;
          this.description = "";
          break;
        case "about":
          t = this.$themeConfig.menus.about || "自我介绍";
          this.isPosts = false;
          this.description = "";
          break;
        default:
          t = this.$site.title || "欢迎光临";
          this.isPosts = false;
          this.description = this.$site.description || "期待与你的交流";
      }
      return t;
    }
  }
};
