result:
.el-menu {
  border-right: none;
}

.el-menu-item.is-active {
  background-color: #ECEEF8;
}

.el-menu-item:hover {
  color: #3f51b5;

  i {
    color: #3f51b5;
  }
}

.iconfont {
  color: #909399;
  margin-right: 19px;
  margin-left: 4px;
  width: 24px;
  text-align: center;
  font-size: 28px;
  vertical-align: middle;
  display: inline-block;
}

.menuHide .iconfont {
  margin-left: -5px;
  transition: 0.5s ease-in-out;
}

.menu {
  position: fixed;
  top: 0;
  left: 0;
  bottom: 0;
  z-index: 66;
  min-height: 100%;
  background: #fff;
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  will-change: transform, -webkit-transform;
  transition: 0.2s ease-in-out;
}

.menuHide .menu-wrap {
  width: 60px;
}

.menu-wrap {
  width: 240px;
  transition: 0.2s ease-in-out;
}

.menu .brand-wrap {
  background-size: 100% 100%;
  background-repeat: no-repeat;
}

.menu .brand {
  padding: 41px 24px 24px;
  background: rgba(63, 81, 181, 0.5);
  transition: 0.2s ease-in-out;
}

.menuHide .brand {
  padding: 8px 4px;
  background: #fff;
}

.menu .avatar {
  transition: 0.2s ease-in-out;
  display: block;
  width: 80px;
  height: 80px;
  border: 2px solid #fff;
  border-radius: 50%;
  overflow: hidden;
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
}

.menuHide .avatar {
  width: 48px;
  height: 48px;
}

.menu .avatar img {
  width: 100%;
  height: 100%;
}

.menu .introduce {
  margin: 1em 0 0;
  color: #fff;
  transition: 0.2s ease-in-out;
}

.menu .mail {
  display: inline-block;
  padding-top: 4px;
  color: #c5cae9;
  font-size: 13px;
}

.menuHide .introduce, .menuHide .item-title {
  display: none;
}

.item-title {
  font-size: 16px;
}

@media (max-width: 1200px) {
  .menu {
    transform: translate(-100%, 0px);
  }

  .menuHide.menu {
    transform: translate(0, 0px);
  }

  .menuHide .menu-wrap {
    width: 240px;
  }

  .menuHide .introduce, .menuHide .item-title {
    display: inline-block;
  }

  .menuHide .avatar {
    width: 80px;
    height: 80px;
  }

  .menuHide .brand {
    padding: 41px 24px 24px;
    background: rgba(63, 81, 181, 0.5);
  }

  .menuHide .iconfont {
    margin-left: 4px;
  }
}
