result:
.post-title-time {
  font-weight: 500;
  font-size: 15px;
  color: #727272;
  margin: 10px 10px 0;
}
.post-title-link {
  color: #3f51b5;
  font-size: 19px;
  padding: 0 10px 10px;
  display: flex;
}
.tag-content {
  margin-top: -38px;
  padding-bottom: 30px;
}

.tag-card {
  padding: 20px;
  background: #fff;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  align-content: center;
  border-radius: 4px;
  box-shadow: 0 8px 10px -5px rgba(0, 0, 0, 0.2),
    0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12);
}

.post-list {
  flex-wrap: wrap;
  justify-content: space-evenly;
}
.list {
  margin: 15px 0;
  &:first-child,
  &:nth-child(2) {
    margin-top: 30px;
  }
}
