.post-content {
  position: relative;
  margin-top: -34px;
  padding-bottom: 40px;
}
.post-card {
  margin-right: 20%;
  padding: 35px;
  background: #fff;
  transition: 0.2s all ease-in-out;
  border-radius: 9px;
  box-shadow: 0 8px 10px -5px rgba(0, 0, 0, 0.2),
    0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12);
}
@media (max-width: 1200px) {
  .post-card {
    margin-right: 0;
  }
}
.post-prev,
.post-next {
  display: flex;
  flex-direction: column;
  font-size: 18px;
  vertical-align: middle;
  i {
    vertical-align: bottom;
  }
  a:hover,
  p:hover {
    background-color: #e8e8e8;
    cursor: pointer;
  }
}
.post-prev {
  text-align: left;
}
.post-next {
  text-align: right;
}
.nav-title {
  color: #3f51b5;
}

.el-col-6 {
  width: 22%;
}
.catalog-title {
  margin-bottom: -10px;
  text-align: center;
  transition: all 0.2s ease-in-out;
}
.post-toc {
  position: absolute;
  margin-left: 45px;
  height: 60vh;
  overflow-y: auto;
  width: 19vw;
  top: 69px;
  right: 24px;
  border-radius: 16px;
  background: #f6f6f6;
}
.fixed {
  position: fixed;
  top: 69px;
}
@media (max-width: 1200px) {
  .post-toc {
    position: fixed;
    top: auto;
    bottom: 70px;
    right: 20px;
    width: 88%;
    transform: translateX(125%);
    z-index: 77;
  }
  .open-toc {
    transform: translateX(0);
  }
  .post-card {
    padding: 13px;
  }
}

@media (max-width: 766px) {
  .post-card {
    border-radius: 0;
    box-shadow: none;
  }
}
.toc-link:before,
.toc-link:after {
  content: "";
  position: absolute;
  z-index: 1;
  top: 0;
  right: 0;
  left: 3px;
  height: 100%;
}
.toc-li.active > .toc-link:after {
  border-left: 3px solid #3f51b5;
  left: 3px;
}
.toc-li.active > .toc-link:before {
  background: rgba(0, 0, 0, 0.06);
}
.catalog-body {
  .catalog-list {
    font-size: 13px;
    list-style: none;
    cursor: pointer;

    .toc-li {
      height: 26px;
    }
    a {
      display: block;
      padding: 3px 5px 3px 9px;
      margin: 4px 0;
      position: relative;
      font-weight: 600;
      font-size: 14px;
      color: inherit;
    }

    .active a {
      color: #3f51b5;
    }
  }
}
